import React, { useEffect } from "react";
import {
  LoginBoxContainer,
  Title,
  TextBox,
  Input,
  SubmitButton,
  SignupTopsection,
  BottomSection,
  SignupButton,
} from "../../style/stylecomponents/MemberStyle/style";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { signupVerify, signupUser } from "../../utils/apimodule/member";
const SignupPage = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [pwd, setPwd] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [successVerify, setSuccessVerify] = useState(true);
  const [name, setName] = useState("");

  const handelSignupClick = async () => {
    if (!email || !pwd || !confirmPassword) {
      alert("아이디와 비밀번호를 입력해주세요.");
      return;
    }

    if (pwd !== confirmPassword) {
      alert("비밀번호가 일치하지 않습니다.");
      return;
    }

    try {
      const result = await signupUser(email, pwd, name);
      if (result.success) {
        navigate("/login");
        alert("회원가입이 완료되었습니다.");
      } else {
        throw new Error("회원가입에 실패했습니다.");
      }
    } catch (error) {
      alert(`회원가입 실패: ${error.message}`);
    }
  };

  const handleSignupVerify = async () => {
    try {
      const result = await signupVerify(email);
      if (result.success) {
        setSuccessVerify(false);
      } else {
        throw result;
      }
    } catch (error) {
      alert(`실패: ${error.message}`);
    }
  };

  return (
    <LoginBoxContainer>
      <SignupTopsection></SignupTopsection>
      <BottomSection>
        <TextBox>
          {successVerify ? (
            <>
              <Input
                type="text"
                placeholder="이메일을 입력하세요"
                required
                name="email"
                onChange={(e) => setEmail(e.target.value)}
              />

              <SignupButton
                type="email"
                placeholder="이메일 중복확인"
                value="이메일 중복확인"
                onClick={handleSignupVerify}
              >
                <p>이메일 중복확인</p>
              </SignupButton>
            </>
          ) : (
            <>
              <Input
                type="text"
                placeholder={email}
                required
                name="email"
                disabled
              />
              <div>
                <p>✅이메일 인증이 완료되었습니다.</p>
              </div>
            </>
          )}

          <Input
            type="text"
            placeholder="이름을 입력하세요"
            name="name"
            required
            onChange={(e) => setName(e.target.value)}
          />

          <Input
            type="password"
            placeholder="패스워드를 입력하세요"
            name="password"
            required
            onChange={(e) => setPwd(e.target.value)}
          />
          <Input
            type="password"
            placeholder="패스워드 재확인"
            name="confirmpassword"
            required
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          {successVerify ? (
            <SubmitButton type="submit" value="회원가입" bgColor="gray" />
          ) : (
            <>
              <SubmitButton
                type="submit"
                value="회원가입"
                onClick={handelSignupClick}
              />
            </>
          )}
        </TextBox>
      </BottomSection>
    </LoginBoxContainer>
  );
};

export default SignupPage;
